dw:transform-message.dw:set-payload:
%dw 1.0
%output application/xml
---
{
	MediaHAVEN_external_metadata: 
	{
		title: flowVars.pid ++ "-" ++ flowVars.mediaId,
		MDProperties: {
			CP: p('cp'),
			sp_name: 'dailies' when flowVars.origin != 'tape' otherwise 'intake_existing',
			PID: flowVars.pid,
			dc_identifier_cpid: flowVars.mediaId,
			(md5: flowVars.md5Xml) when flowVars.md5Xml?
		}
	}
}

%dw 1.0
%output application/xml
%namespace ns0 http://www.jcp.org/jcr/1.0
%namespace ns6 http://purl.org/dc/elements/1.1/
%namespace ns11 http://www.day.com/jcr/cq/1.0
%namespace ns12 http://dublincore.org/documents/dcmi-terms/
%function split(name) name splitBy ", "
%namespace ns4 http://iptc.org/std/Iptc4xmpExt/2008-02-29/
%namespace ns7 http://www.loc.gov/standards/premis/v2/premis.xsd
%namespace ns2 http://ns.useplus.org/ldf/xmp/1.0/
%namespace ns8 http://iptc.org/std/Iptc4xmpCore/1.0/xmlns/
%namespace ns9 http://ns.adobe.com/photoshop/1.0/
---
{
	MediaHAVEN_external_metadata: {
		(title: payload.xml.ns0#content.metadata.ns6#title when (payload.xml.ns0#content.metadata.ns6#title? and payload.xml.ns0#content.metadata.ns6#title != "") 
			otherwise (payload.xml.ns0#content.metadata.ns4#ArtworkOrObject."_x0031_".ns4#AOSourceInvNo 
				when payload.xml.ns0#content.metadata.ns4#ArtworkOrObject."_x0031_".ns4#AOSourceInvNo? otherwise ""))
				when payload.xml.ns0#content.metadata.ns6#title != null or payload.xml.ns0#content.metadata.ns4#ArtworkOrObject."_x0031_".ns4#AOSourceInvNo != null,
		description: payload.xml.ns0#content.metadata.ns6#description when (payload.xml.ns0#content.metadata.ns6#description?) otherwise "",
		MDProperties: {
			sp_name: 'borndigital',
			CP: flowVars.CP,
			PID: flowVars.pid,
			CreationDate: payload.xml.ns0#content.metadata.ns6#date when payload.xml.ns0#content.metadata.ns6#date? otherwise "",
			(dc_title: payload.xml.ns0#content.metadata.ns6#title when (payload.xml.ns0#content.metadata.ns6#title? and payload.xml.ns0#content.metadata.ns6#title != "") 
					otherwise (payload.xml.ns0#content.metadata.ns4#ArtworkOrObject."_x0031_".ns4#AOSourceInvNo 
						when payload.xml.ns0#content.metadata.ns4#ArtworkOrObject."_x0031_".ns4#AOSourceInvNo? otherwise ""))
					when payload.xml.ns0#content.metadata.ns6#title != null or payload.xml.ns0#content.metadata.ns4#ArtworkOrObject."_x0031_".ns4#AOSourceInvNo != null,
			dcterms_created: payload.xml.ns0#content.metadata.ns6#date when payload.xml.ns0#content.metadata.ns6#date? otherwise "",
			dc_localid: payload.xml.ns0#content.metadata.custom_uuid when payload.xml.ns0#content.metadata.custom_uuid? otherwise "",
			dc_identifier_localids @(type: "list"): {
				(SourceInvNo: payload.xml.ns0#content.metadata.ns4#ArtworkOrObject."_x0031_".ns4#AOSourceInvNo) when payload.xml.ns0#content.metadata.ns4#ArtworkOrObject."_x0031_".ns4#AOSourceInvNo?,
				(SourceInvNo: payload.xml.ns0#content.metadata.ns4#ArtworkOrObject."_x0032_".ns4#AOSourceInvNo) when payload.xml.ns0#content.metadata.ns4#ArtworkOrObject."_x0032_".ns4#AOSourceInvNo?
			},
			dc_rights_rightsOwners @(type: "list"): {
				(rechtenhouder: payload.ns0#content.metadata.ns2#CopyrightOwner."_x0031_".ns2#CopyrightOwnerName) when payload.ns0#content.metadata.ns2#CopyrightOwner."_x0031_".ns2#CopyrightOwnerName?,
				(rechtenhouder: payload.ns0#content.metadata.ns2#CopyrightOwner."_x0032_".ns2#CopyrightOwnerName) when payload.ns0#content.metadata.ns2#CopyrightOwner."_x0032_".ns2#CopyrightOwnerName?
			},
			dc_contributors @(type: "list"): {
				(contributor: payload.xml.ns0#content.metadata.ns6#contributor) when payload.xml.ns0#content.metadata.ns6#contributor?	
			},
			dc_creators @(type: "list"): {
				(creator: payload.xml.ns0#content.metadata.ns6#creator) when payload.xml.ns0#content.metadata.ns6#creator?
			},
			(dc_subjects @(type: "list"): {
				(
					split(payload.xml.ns0#content.metadata.ns6#subject) map {
						trefwoord: $
					}
				)
			}) when payload.xml.ns0#content.metadata.ns6#subject?,
			(dc_coverages @(type: "list"): {
				(
					split(payload.xml.ns0#content.metadata.ns8#Location) map {
						ruimte: $
					}
				)
			}) when payload.xml.ns0#content.metadata.ns8#Location?,
			(dc_rights_credit: payload.xml.ns0#content.metadata.ns9#credit) when payload.xml.ns0#content.metadata.ns9#credit?,
			dc_description: payload.xml.ns0#content.metadata.ns6#description when (payload.xml.ns0#content.metadata.ns6#description?) otherwise "",
			// Not found:
			(dc_rights_comment: payload.xml.ns0#content.metadata.ns2#OtherImageInfo) when payload.xml.ns0#content.metadata.ns2#OtherImageInfo?,
			(md5: payload.xml.md5) when payload.xml.md5?
		}
	}
}
